mapper.select:


     and sml.message_key like concat(concat('%', #{messageKey}), '%')
     and sml.message_template_name like concat(concat('%', #{messageTemplateName}), '%')
     and sml.message_type = #{messageType}
     and sml.template_mode = #{templateMode}
     and sml.account like concat(concat('%', #{account}), '%')
     and sml.template_id like concat(concat('%', #{templateId}), '%')
     and sml.supplier_type = #{supplierType}
     and sml.is_success = #{isSuccess}
    and sml.log_time between #{params.beginLogTime} and #{params.endLogTime}

order by sml.log_time desc
