mapper.select:


     and sol.user_id = #{userId}
     and sol.title like concat(concat('%', #{title}), '%')
     and sol.business_type = #{businessType}

        #{item}

     and sol.oper_name like concat(concat('%', #{operName}), '%')
     and sol.oper_ip like concat(concat('%', #{operIp}), '%')
     and sol.status = #{status}
    and sol.oper_time between #{params.beginOperTime} and #{params.endOperTime}

