mapper.select:


    and r.del_flag = '0'
     and r.role_id = #{roleId}
     and r.role_name like concat(concat('%', #{roleName}), '%')
     and r.role_key like concat(concat('%', #{roleKey}), '%')
     and r.status = #{status}
    and r.create_time between #{params.beginTime} and #{params.endTime}

order by r.role_sort, r.create_time desc
